public class LogT {
    static final String TAG_DEFAULT = "wyh";
    //    static final String TAG_LINE = "";
//    static final String TAG_LINE = "************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************************";
    static final String TAG_LINE = "--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------";
    static final boolean SWITCH_LOG = true;

    static final boolean SWITCH_SAVE_LOG = false;
    static final String FILE_LOG = "d:/log_save/clog.txt";


    public static void d(String msg) {
        d("", msg);
    }

    public static void d(String tag, String msg) {
        if (!SWITCH_LOG) return;

        String logString = msg;
        if (!"".equals(tag.trim())) logString = "[>>" + tag + "<<] : " + msg;
        int lineLength = logString.getBytes().length - (logString.getBytes().length - logString.length()) / 2;

        // TODO Android下可换为Log并添加TAG
        System.out.println(TAG_LINE.substring(0, lineLength));
        System.out.println(logString);
        System.out.println(TAG_LINE.substring(0, lineLength));
    }


    public static void net(String url, Map<String, String> map) {
        net("", url, map);
    }

    public static void net(String tag, String url, Map<String, String> map) {
        if (!SWITCH_LOG) return;

        String mapString = "";
        if (null != map)
            for (String key : map.keySet()) {
                mapString += key + "→" + map.get(key) + " || ";
            }

        String logString = url + " : " + mapString;
        if (!"".equals(tag.trim())) logString = "[>>" + tag + "<<] : " + logString;
        int lineLength = logString.getBytes().length - (logString.getBytes().length - logString.length()) / 2;
        if (logString.endsWith(" || ")) {
            logString = logString.substring(0, logString.lastIndexOf(" || "));
            lineLength -= 4;
        }

        System.out.println(TAG_LINE.substring(0, lineLength));
        System.out.println(logString);
        System.out.println(TAG_LINE.substring(0, lineLength));
    }


    public static void main(String[] args) {
        d("asdfa撒旦法dfds23as算法saf=");
        d("LogT", "asdfa撒旦法dfds23as算法saf");


        String url = "http://127.0.0.1/aesadf/wer/asf/adf";
        Map<String, String> map = new HashMap<>();
        map.put("1adfsd速度1", "1突然sdf1");
        map.put("2adfsd速度2", "2u人sdf2");
        map.put("3adfsd速度3", "3与sdf3");
        net(url, map);
        net("LogT", url, map);
    }
}
